/* eslint-env node */
// NOTE: This file is run pre build and creates files needed to build the static site.
const path = require('path');
const { createPagesFile } = require('@creuna/codegen');

const eslintConfig = require('../.eslintrc.json');

const disclaimers = [
  '// NOTE: Do not edit this file. It is built by running `./scripts/create-static-site-files.js`'
].join('\n');

const pagesPath = path.join(__dirname, '..', 'source', 'static-site', 'pages');

createPagesFile({
  pagesPath,
  fileHeader: disclaimers,
  fileName: 'pages.js',
  outputPath: pagesPath,
  prettierOptions: eslintConfig.rules['prettier/prettier'][1]
});
